mapper.insert:
insert into infrMember(
	ifmmId
	,ifmmName
	,ifmmGenderCd
	,ifmmPassword
	,ifmmDob
	,ifmmSmsConsentNy
	,ifmmEmailConsentNy
	,ifmmSavedCd
	,ifmmMarriageCd
	,ifmmMarriageDate
	,ifmmChildrenNum
	,ifmmFavoriteColor
	,ifmmDesc
	,ifmmRecommenderSeq
	,ifmmDelNy
	,regDateTime
	,modDateTime
) values (
	#{ifmmId}
	,#{ifmmName}
	,#{ifmmGenderCd}
	,#{ifmmPassword}

		,NULL
		,#{ifmmDob}


	,#{ifmmSmsConsentNy}
	,#{ifmmEmailConsentNy}
	,#{ifmmSavedCd}
	,#{ifmmMarriageCd}
	,#{ifmmMarriageDate}


		,#{ifmmChildrenNum}
		,null

	,#{ifmmFavoriteColor}
	,#{ifmmDesc}


		,(select a.ifmmSeq from infrMember a where a.ifmmName LIKE #{ifmmRecommenderName})
		,null

	,0
	,#{regDateTime}
	,#{modDateTime}

)

	SELECT last_insert_id()

insert into infrMemberEmail(
	ifmmSeq
	,ifmeEmailAccount
	,ifmeEmailDomainCd
	,ifmeEmailFull
	,ifmeDefaultNy
	,ifmeDelNy
) values (
	#{ifmmSeq} 
	,#{ifmeEmailAccount}
	,#{ifmeEmailDomainCd}
	,concat(#{ifmeEmailAccount}, "@", (select ifcdName from infrCode where #{ifmeEmailDomainCd} = ifcdSeq))
	,1
	,0
)

mapper.update:
UPDATE
	infrMember
SET 
	ifmmPassword = #{ifmmPassword}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE
	infrMember
SET 
	ifmmSmsConsentNy = #{ifmmSmsConsentNy}
	,ifmmEmailConsentNy = #{ifmmEmailConsentNy}
	,ifmmSavedCd = #{ifmmSavedCd}
	,ifmmMarriageCd = #{ifmmMarriageCd}
	,ifmmMarriageDate = #{ifmmMarriageDate}
	,ifmmChildrenNum = #{ifmmChildrenNum}
	,ifmmFavoriteColor = #{ifmmFavoriteColor}
	,ifmmDesc = #{ifmmDesc}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE
	infrMemberAddress
SET 
	ifmaZipcode = #{ifmaZipcode}
	,ifmaAddress1 = #{ifmaAddress1}
	,ifmaAddress2 = #{ifmaAddress2}	
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
	AND ifmaDefaultNy = 1

UPDATE
	infrMemberEmail
SET 
	ifmeEmailAccount = #{ifmeEmailAccount}
	,ifmeEmailDomainCd = #{ifmeEmailDomainCd}
	,ifmeEmailFull = concat(#{ifmeEmailAccount}, "@", (select ifcdName from infrCode where #{ifmeEmailDomainCd} = ifcdSeq))
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
	AND ifmeDefaultNy = 1

UPDATE
	infrMemberJoinQna
SET 
	ifjqQuestionCd = #{ifjqQuestionCd}
	,ifjqAnswer = #{ifjqAnswer}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE
	infrMemberAddressOnline
SET 
	ifaoSnsTypeCd = #{ifaoSnsTypeCd}
	,ifaoUrl = #{ifaoUrl}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
	AND ifaoDefaultNy = 1

UPDATE
	infrMemberHobby
SET 
	ifmhHobbyCd = #{ifmhHobbyCd}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
